<style>
  section {
    position: relative;
  }
  .intro {
    min-height: calc(100vh - 5rem);
    display: grid;
    grid-template-columns: 1fr 1fr;
    align-items: center;
  }
  h1 {
    margin: 2rem;
    max-width: 28ch;
    font-size: 3rem;
    text-transform: uppercase;
    font-weight: 600;
    line-height: 1.25;
    letter-spacing: -.05rem;
  }
  .features {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 2.5rem;
  }
  .features a {
    position: relative;
    padding: 2.5rem;
    display: grid;
    grid-template-columns: 1fr 2fr;
    align-items: flex-start;
    gap: 1.5rem;
    background: black;
    text-decoration: none;
    border-radius: 1.5rem;
    transition: all .25s linear;
    cursor: pointer;
  }
  h2 {
    font-size: 1.5rem;
    font-weight: 600;
    text-transform: uppercase;
  }
  p {
    font-size: 1.25rem;
  }
  .illustration {
    width: 100%;
    height: 100vh;
    display: grid;
    place-content: center;
  }
  .illustration svg {
    width: 70%;
  }
  @media (max-width: 60em) {
    h1 {
      font-size: 2rem;
    }
    .right {
      display: flex;
      flex-direction: column;
    }
  }
</style>