<style>
  .grid-2 {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1.5rem;
  }
  .intro h1 {
    margin-bottom: 1rem
  }
  .illustration {
    width: 100%;
    padding-top: 100%;
    border-radius: 1rem;
    background: white;
  }
  .category-grid {
    margin: 4rem 0;
    font-size: 1.5rem;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 1.5rem;
  }
  .category {
    position: relative;
    padding: 2rem;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1rem;
    background: linear-gradient(135deg, var(--night-2), var(--night-3));
    box-shadow: 0 0 1rem hsla(0,0%,0%,0.5);
    border-radius: 1.5rem;
  }
  .category h2 {
    font-weight: normal;
  }
  .category svg  {
    width: 1.5rem;
    height: 1.5rem;
    padding: .75rem;
    fill: var(--day-1);
    background: var(--blue-4);
    box-shadow: 0 0 .5rem hsla(0,0%,0%,0.5);
    border-radius: 1rem;
  }
  @media (max-width:60em) {
    .grid-2 {
      grid-template-columns: 1fr;
    }
    .illustration {
      display: none;
    }
  }
</style>